export default function ProductScreen(props) {
    const product = data.products.find((x) => x._id === props.match.params.id);
    if(!product){
        <div>Product Not Found</div>
    }
    return <div>
        <div className="row">
            <div className="col-2">
                <img className src={product.img} alt={product.name}></img>
            </div>
            <div className="col-1">

            </div>
            <div className="col-1">

            </div>
        </div>
    </div>;
}
